import React, { useState } from 'react';
import './App.css';
import './main.scss';
import Header from './components/Header';
import Footer from './components/Footer';
import DishUpload from './components/DishUpload';
import DishList from './components/DishList';
import Login from './components/Login';
import Register from './components/Register';

function App() {
  const [user, setUser] = useState(null); // null, 'user', 'admin'
  const [dishes, setDishes] = useState([
    { id: 1, name: 'Pizza', description: 'Delicious cheese pizza', price: 9.99, image: 'sample-dish.jpg' },
    { id: 2, name: 'Pasta', description: 'Tasty pasta with tomato sauce', price: 7.99, image: 'sample-dish.jpg' },
  ]);

  const handleLogin = (role) => {
    setUser(role);
  };

  return (
    <div className="App">
      <Header user={user} setUser={setUser} />
      {!user ? (
        <>
          <Login onLogin={handleLogin} />
          <Register />
        </>
      ) : (
        <>
          {user === 'admin' && <DishUpload setDishes={setDishes} />}
          <DishList user={user} dishes={dishes} setDishes={setDishes} />
        </>
      )}
      <Footer />
    </div>
  );
}

export default App;
