import React, { useState } from 'react';

const DishUpload = ({ setDishes }) => {
  const [dishName, setDishName] = useState('');
  const [dishDescription, setDishDescription] = useState('');
  const [dishPrice, setDishPrice] = useState('');
  const [dishImage, setDishImage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    setDishes(prevDishes => [
      ...prevDishes,
      { id: prevDishes.length + 1, name: dishName, description: dishDescription, price: parseFloat(dishPrice), image: dishImage }
    ]);
    setDishName('');
    setDishDescription('');
    setDishPrice('');
    setDishImage('');
  };

  return (
    <div className="dish-upload">
      <h2>Įkelti Patiekalą</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Patiekalo pavadinimas:</label>
          <input
            type="text"
            value={dishName}
            onChange={(e) => setDishName(e.target.value)}
          />
        </div>
        <div>
          <label>Patiekalo aprašymas:</label>
          <textarea
            value={dishDescription}
            onChange={(e) => setDishDescription(e.target.value)}
          />
        </div>
        <div>
          <label>Kaina:</label>
          <input
            type="number"
            value={dishPrice}
            onChange={(e) => setDishPrice(e.target.value)}
          />
        </div>
        <div>
          <label>Nuotraukos URL:</label>
          <input
            type="text"
            value={dishImage}
            onChange={(e) => setDishImage(e.target.value)}
          />
        </div>
        <button type="submit">Įkelti</button>
      </form>
    </div>
  );
}

export default DishUpload;
